import React, { useEffect, useState } from "react";
import Quotes from "./Quotes";
import api from "./services/api";
import "../stylesheets/App.css";

function App() {
  const [quote, setQuote] = useState("");
  useEffect(() => {
    api.getDataFromApi().then((data) => {
      setQuote(data.sentence);
    });
  }, []);
  const handleClick = () => {
    api.getDataFromApi().then((data) => {
      setQuote(data.sentence);
      console.log(data);
    });
  };

  return (
    <div className="App">
      <div className="title-box">
        <h1 className="title-box__title">Choose your Game of Thrones Quote</h1>
      </div>
      <Quotes quote={quote} handleClick={handleClick} sentence="Quote" />
    </div>
  );
}

export default App;
