import "../stylesheets/App.css";
import React, { useState } from "react";

function App() {
  const [count, setCount] = useState(0);

  const handleIncrement = () => {
    setCount((prevCount) => prevCount + 1);
  };

  const handleDecrement = () => {
    setCount((prevCount) => prevCount - 1);
  };
  return (
    <div className="App">
      <button oncClick={handleIncrement}>Button plus</button>
      <span>Count</span>
      <button oncClick={handleDecrement}>Button less</button>
    </div>
  );
}

export default App;
