import React, { useEffect, useState } from "react";
import Quotes from "./Quotes";
import api from "./services/api";

function App() {
  const [quote, setQuote] = useState("");
  useEffect(() => {
    api.getDataFromApi().then((data) => {
      setQuote(data);
    });
  }, []);
  const handleClick = (click) => {
    console.log(click);

    setQuote(api.getDataFromApi());
  };

  return (
    <div className="App">
      <h1>Hello world</h1>
      <Quotes quote={quote} handleClick={handleClick} />
    </div>
  );
}

export default App;
