import React, { useState, useEffect } from "react";
import twitterIcon from "../images/twitterIcon.svg";
import "../stylesheets/Quotes.css";

const Quotes = (props) => {
  const [quote, setQuote] = useState([]);
  const [character, setCharacter] = useState([]);

  useEffect(() => {
    getDataFromApi();
  }, []);
  const getDataFromApi = () => {
    return fetch("https://game-of-thrones-quotes.herokuapp.com/v1/random")
      .then((response) => response.json())
      .then((data) => {
        setQuote(data.sentence);
        console.log(data.character);

        setCharacter(data.character);
      });
  };
  const handleClick = () => {
    getDataFromApi();
  };

  const nameCharac = character.map((eachCharac) => eachCharac.character);
  console.log(nameCharac);

  return (
    <div id="quote-box">
      <div id="text">
        <p>{quote}</p>
      </div>
      <div id="author">
        <p>hola</p>
      </div>

      <div id="buttons">
        <div className="social-media">
          <a href="www.twitter.es" id="twet-quote">
            <span>
              <img className="logo" src={twitterIcon} alt="" />
            </span>
          </a>
        </div>
        <button id="new-quote" onClick={handleClick}>
          New Quote
        </button>
      </div>
    </div>
  );
};
Quotes.propTypes = {};
export default Quotes;
